        binding.recyclerView.layoutManager = LinearLayoutManager(this)

        newsArrayList = arrayListOf<News>()

        for (eachindex in newsImages.indices){

            val news = News(newsHeading[eachindex], newsImages[eachindex], newsDetails[eachindex])

            newsArrayList.add(news)

        }
            var myAdapter = MyAdapter(newsArrayList, this)
            binding.recyclerView.adapter = myAdapter

            myAdapter.setItemClickLitener(object : MyAdapter.onItemClickListener {
                override fun onItemClick(position: Int) {

                    val intent = Intent(this@MainActivity, NewsDetails::class.java)

                    intent.putExtra("NewsHeading", newsArrayList[position].newsHeading)
                    intent.putExtra("NewsImage", newsArrayList[position].newsImage)
                    intent.putExtra("NewsDetails", newsArrayList[position].newsdata)

                    startActivity(intent)
                }


            })

